package com.gagsn.db.dto.mapper;import com.gagsn.db.dto.PublicationTitle;import org.springframework.jdbc.core.RowMapper;import java.sql.ResultSet;import java.sql.SQLException;public class PublicationTitleMapper implements RowMapper<PublicationTitle> {    @Override    public PublicationTitle mapRow(ResultSet rs, int rowNum) throws SQLException {        return new PublicationTitle(rs.getString("ID"), rs.getString("Displaycode"));    }}